import './Table.css';
import React from 'react';

function checkout()
{
    return (
        <button style={{
            color:'white' ,
            backgroundColor: 'rgb(19, 201, 28)',
            height: '40px',
            width:'200px',
            border: 'none',
            fontSize:'20px'
        }}> checkout</button>
    )
}

function item()
{
    return (
        <button onClick={() => item_description()}>Item</button>
    )
}

function item_description()
{
    document.getElementsByClassName('item_description').innerHTML="1";
}

function Table(props)
{
    return (
        <div className="Table">
            <div className="Table_number">
                    Table {`${props.tabledata.number}`}
            </div>
            <div className="Table_status" style={{
                color: props.tabledata.status === "busy" ? "green" : "blue",
            }}
            >
                Status : {`${props.tabledata.status}`}
            </div>
            <div className='Table_item'>
                <div className='item'>
                    {
                        props.tabledata.status ==="busy" ? item(): null
                    }
                </div>
                <div className='item_description'>
                </div>
            </div>
            <div className="Table_checkout">
                {
                    props.tabledata.status ==="busy" ? checkout(): null
                }
            </div>
        </div>
    )
}

export default Table;
